import 'https://odajs.org/oda.js';

ODA({ is: 'oda-todo', imports: '@oda/button',
    template: `
    	<style>
			:host {
				display: flex;
				flex-direction: column;
			}
			h3 {
				color: red;
				border-bottom: 1px solid lightgray;
				padding-bottom: 8px;
			}
			.row {
				margin: 4px;
				padding: 4px;
				border: 1px solid lightgray;
				flex: 1;
				cursor: pointer;
			}
		</style>
    	<h3>oda-ToDo</h3>
    	<oda-button icon="icons:add" @tap="onclickAdd()"></oda-button>
		<div ~for="_json.list" style="display: flex; align-items: center">
			<div contenteditable class="row" @dblclick="item.check = !item.check" ~style="{'text-decoration': item.check ? 'line-through' : 'none'}" @input="item.todo = $event.target.innerText">{{item.todo}}</div>
			<oda-button icon="icons:close"  @click="onclickClose(item)"></oda-button>
		</div>

    `,
    // json - JSON Object from tab 'json'
    get _json() { return json },
    onclickAdd() {
    	this._json.list.push({ "todo" : 'new todo', 'check': false });
    },
    onclickClose(i) {
    	let idx = this._json.list.indexOf(i);
    	this._json.list.splice(idx, 1);
    }
})

